(select uid,gid,concat('/mail/v3store/quarantine/',client_users.client_id,'/.INBOX.',client_users.email_address) as home from client_users,pop_accounts where client_users.id =   pop_accounts.client_user_id and client_domain_id = 0 and email_address= SUBSTRING_INDEX('ceo@hei.co.in','@',1)) union (select uid,gid,concat('/mail/v3store/quarantine/',client_users.client_id,'/.INBOX.',client_users.email_address,'@',client_domains.name) as home from client_users,pop_accounts,client_domains where client_users.id =   pop_accounts.client_user_id and client_users.client_domain_id = client_domains.id and email_address = SUBSTRING_INDEX('ceo@hei.co.in','@',1) and client_domains.name = SUBSTRING_INDEX('ceo@hei.co.in','@',-1)) union (select uid,gid,IF(client_users.client_domain_id = 0, concat('/mail/v3store/quarantine/',client_users.client_id,'/.INBOX.',client_users.email_address)  , concat('/mail/v3store/quarantine/',client_users.client_id,'/.INBOX.',client_users.email_address,'@',cu_client_domains.name) ) as home   FROM pop_email_aliases LEFT JOIN client_domains ON (pop_email_aliases.client_domain_id = client_domains.id) LEFT JOIN pop_accounts ON (pop_email_aliases.pop_account_id = pop_accounts.id) LEFT JOIN client_users ON (pop_accounts.client_user_id = client_users.id) LEFT JOIN client_domains AS cu_client_domains ON (client_users.client_domain_id = cu_client_domains.id) WHERE pop_email_aliases.email_address = SUBSTRING_INDEX('ceo@hei.co.in','@',1) and client_domains.name = SUBSTRING_INDEX('ceo@hei.co.in','@',-1)) union (select 99 as uid,99 as gid,Home as home FROM Quarantine_v03.TrackSession WHERE Login='ceo@hei.co.in') limit 1


